mapper.select:
 	select product_category_id as id,product_category_name as name,detail_image as imageUrl from product where 
 	shop_id=#{shopId,jdbcType=INTEGER}
	GROUP BY product_category_id,product_category_name,detail_image

select 

,

from product
where shop_id=#{shopId,jdbcType=INTEGER} 

  and  product_category_two_id = #{productCategoryTwoId,jdbcType=BIGINT}


  and  product_category_third_id = #{productCategoryThirdId,jdbcType=BIGINT}

